def getBnbPrice():
    session = Session()
    response = session.get('https://api.bscscan.com/api?module=stats&action=bnbprice&apikey=GQ6WM7IE7BZ3IK4KNVVM9HM5NR2BIDCFEA')
    data = json.loads(response.text)
    return data['result']['ethusd']

def getBscUniPool(contract):
    reserves = contract.functions.getReserves().call()
    d = dict()
    d['q0'] = Decimal(reserves[0])
    d['q1'] = Decimal(reserves[1])
    d['decimal'] = contract.functions.decimals().call()
    d['token0'] = contract.functions.token0().call()
    d['token1'] = contract.functions.token1().call()
    d['totalSupply'] = contract.functions.totalSupply().call()
    return d

